INSERT INTO `tbconteudo` (`conId`, `conTitulo`, `conTexto`, `conFonte`, `conData`, `conCategoria`, `conDestaque`, `conAtivo`) VALUES("14", "Recados", "\r\n                <font style=\"font-family: Times New Roman,Times,serif;\" size=\"2\"><span style=\"font-weight: bold;\">    <br>Prazo encerrado</span> para o envio de propostas para apresentação de trabalhos<span style=\"font-weight: bold;\"></span>.<span style=\"font-weight: bold;\"><br><br></span><span style=\"font-weight: bold;\"></span><a href=\"http://www.packtpub.com/award/banner\"><font size=\"3\"><span style=\"font-weight: bold;\">Veja aqui o RESULTADO do CMS ganhador ...</span></font></a><a href=\"http://www.encontroanpocs.org.br/2006/gts_sts_aprovados.asp\"><font size=\"3\"><strong></strong></font></a><span style=\"font-weight: bold;\"><br></span><span style=\"font-weight: bold;\"><span></span><p></p>  <p></p>  </span><span style=\"font-weight: bold;\">20 de agosto:</span> prazo para pagamento das inscrições dos participantes com apresentação de trabalhos.<br><span style=\"font-weight: bold;\"><br>20 de agosto</span>: início das inscrições para o <span style=\"font-weight: bold;\">Lançamento de Livros</span>.<br><span style=\"font-weight: bold;\"><br></span><span style=\"font-weight: bold;\">28 de setembro</span>: Prazo máximo para encaminhamento da íntegra dos <span style=\"font-weight: bold;\">trabalhos aceitos</span>.<br><span style=\"font-weight: bold;\"><br>28 de setembro: </span>Prazo máximo para encaminhamento dos <span style=\"font-weight: bold;\">posters</span> <span style=\"font-weight: bold;\">aceitos</span>.<span style=\"font-weight: bold;\"><br><br>10 de outubro</span>: Prazo máximo para <span style=\"font-weight: bold;\">inscrição antecipada</span>. Aproveite os descontos.<br><br><br>  <p></p></font>  <div style=\"text-align: left;\"><br></div>                \r\n", "Autor(a)", "2006-08-21", "3", "", "S");
INSERT INTO `tbconteudo` (`conId`, `conTitulo`, `conTexto`, `conFonte`, `conData`, `conCategoria`, `conDestaque`, `conAtivo`) VALUES("18", "Transporte Terrestre", "\r\n  <h2>  TRANSPORTE TERRESTRE</h2><span style=\"font-weight: bold;\">ÔNIBUS</span>: a Organização disponibilizará ônibus para seus associados<span style=\"color: rgb(255, 0, 0);\"></span>. Breve estará disponível maiores informações.<br><span lang=\"PT-BR\"><span style=\"color: black;\"><br><span style=\"font-weight: bold;\">ÔNIBUS INTERESTADUAL</span>: o trajeto até São Paulo pode ser feito de diversas formas<span>. A cidade possui tres rodoviarias: Tiete, Barra Funda e Jabaquara. Breve maiores informações.<br></span></span></span>\r\n", "Autor(a)", "2006-08-25", "9", "", "S");
